mapper.update:
UPDATE tb_goods_sku
SET inventory = inventory - #{inventory, jdbcType=INTEGER}
WHERE id = #{id, jdbcType=BIGINT}
AND inventory >= #{inventory, jdbcType=INTEGER}
AND is_deleted = 1

UPDATE tb_goods_sku
SET inventory = inventory + #{inventory, jdbcType=INTEGER}
WHERE id = #{id, jdbcType=BIGINT}
AND is_deleted = 1
