mapper.insert:

  select LAST_INSERT_ID()

insert into tb_bargain_goods (bargain_activity_id, validity_date_type, 
  goods_sku_code, price, inventory, 
  create_time, update_time, is_deleted
  )
values (#{bargainActivityId,jdbcType=BIGINT}, #{validityDateType,jdbcType=TINYINT}, 
  #{goodsSkuCode,jdbcType=VARCHAR}, #{price,jdbcType=DECIMAL}, #{inventory,jdbcType=INTEGER}, 
  #{createTime,jdbcType=TIMESTAMP}, #{updateTime,jdbcType=TIMESTAMP}, #{isDeleted,jdbcType=BIT}
  )

mapper.select:
SELECT

  ${returnFields}




FROM tb_bargain_goods

    AND id = #{id,jdbcType=BIGINT}

        #{item,jdbcType=BIGINT}

    AND bargain_activity_id = #{bargainActivityId,jdbcType=BIGINT}

        #{item,jdbcType=BIGINT}

    AND validity_date_type = #{validityDateType,jdbcType=TINYINT}

        #{item,jdbcType=TINYINT}

    AND goods_sku_code = #{goodsSkuCode,jdbcType=VARCHAR}
    AND goods_sku_code LIKE concat(#{goodsSkuCodeLike,jdbcType=VARCHAR},'%')

        #{item,jdbcType=VARCHAR}

    AND price >= #{priceFrom,jdbcType=DECIMAL}
    AND price <= #{priceTo,jdbcType=DECIMAL}
    AND inventory = #{inventory,jdbcType=INTEGER}

        #{item,jdbcType=INTEGER}

    AND create_time >= #{createTimeFrom,jdbcType=TIMESTAMP}
    AND create_time <= #{createTimeTo,jdbcType=TIMESTAMP}
    AND update_time >= #{updateTimeFrom,jdbcType=TIMESTAMP}
    AND update_time <= #{updateTimeTo,jdbcType=TIMESTAMP}
    AND is_deleted = #{isDeleted,jdbcType=BOOLEAN}

        #{item,jdbcType=BOOLEAN}



    order by ${orderBy}
    order by id
