mapper.select:
SELECT

  ${returnFields}




FROM tb_coupon

    AND id = #{id,jdbcType=BIGINT}

        #{item,jdbcType=BIGINT}

    AND shop_id = #{shopId,jdbcType=BIGINT}

        #{item,jdbcType=BIGINT}

    AND coupon_name = #{couponName,jdbcType=VARCHAR}
    AND coupon_name LIKE concat(#{couponNameLike,jdbcType=VARCHAR},'%')

        #{item,jdbcType=VARCHAR}

    AND coupon_package_id = #{couponPackageId,jdbcType=BIGINT}

        #{item,jdbcType=BIGINT}

    AND use_type = #{useType,jdbcType=TINYINT}

        #{item,jdbcType=TINYINT}

    AND price >= #{priceFrom,jdbcType=DECIMAL}
    AND price <= #{priceTo,jdbcType=DECIMAL}
    AND person_limit = #{personLimit,jdbcType=INTEGER}

        #{item,jdbcType=INTEGER}

    AND order_limit = #{orderLimit,jdbcType=INTEGER}

        #{item,jdbcType=INTEGER}

    AND start_time >= #{startTimeFrom,jdbcType=TIMESTAMP}
    AND start_time <= #{startTimeTo,jdbcType=TIMESTAMP}
    AND end_time >= #{endTimeFrom,jdbcType=TIMESTAMP}
    AND end_time <= #{endTimeTo,jdbcType=TIMESTAMP}
    AND pending_time >= #{pendingTimeFrom,jdbcType=TIMESTAMP}
    AND pending_time <= #{pendingTimeTo,jdbcType=TIMESTAMP}
    AND inventory = #{inventory,jdbcType=INTEGER}

        #{item,jdbcType=INTEGER}

    AND coupon_type = #{couponType,jdbcType=TINYINT}

        #{item,jdbcType=TINYINT}

    AND promotion_amount >= #{promotionAmountFrom,jdbcType=DECIMAL}
    AND promotion_amount <= #{promotionAmountTo,jdbcType=DECIMAL}
    AND order_full_amount >= #{orderFullAmountFrom,jdbcType=DECIMAL}
    AND order_full_amount <= #{orderFullAmountTo,jdbcType=DECIMAL}
    AND promotion_max_amount >= #{promotionMaxAmountFrom,jdbcType=DECIMAL}
    AND promotion_max_amount <= #{promotionMaxAmountTo,jdbcType=DECIMAL}
    AND discount_amount >= #{discountAmountFrom,jdbcType=DECIMAL}
    AND discount_amount <= #{discountAmountTo,jdbcType=DECIMAL}
    AND coupon_status = #{couponStatus,jdbcType=TINYINT}

        #{item,jdbcType=TINYINT}

    AND create_time >= #{createTimeFrom,jdbcType=TIMESTAMP}
    AND create_time <= #{createTimeTo,jdbcType=TIMESTAMP}
    AND update_time >= #{updateTimeFrom,jdbcType=TIMESTAMP}
    AND update_time <= #{updateTimeTo,jdbcType=TIMESTAMP}
    AND is_deleted = #{isDeleted,jdbcType=BOOLEAN}

        #{item,jdbcType=BOOLEAN}



    order by ${orderBy}
    order by id
