mapper.select:
SELECT

  ${returnFields}




FROM tb_deliver_address

    AND id = #{id,jdbcType=BIGINT}

        #{item,jdbcType=BIGINT}

    AND unique_user_id = #{uniqueUserId,jdbcType=VARCHAR}
    AND unique_user_id LIKE concat(#{uniqueUserIdLike,jdbcType=VARCHAR},'%')

        #{item,jdbcType=VARCHAR}

    AND consignee = #{consignee,jdbcType=VARCHAR}
    AND consignee LIKE concat(#{consigneeLike,jdbcType=VARCHAR},'%')

        #{item,jdbcType=VARCHAR}

    AND mobile = #{mobile,jdbcType=VARCHAR}
    AND mobile LIKE concat(#{mobileLike,jdbcType=VARCHAR},'%')

        #{item,jdbcType=VARCHAR}

    AND zip_code = #{zipCode,jdbcType=VARCHAR}
    AND zip_code LIKE concat(#{zipCodeLike,jdbcType=VARCHAR},'%')

        #{item,jdbcType=VARCHAR}

    AND province_id = #{provinceId,jdbcType=INTEGER}

        #{item,jdbcType=INTEGER}

    AND province_name = #{provinceName,jdbcType=VARCHAR}
    AND province_name LIKE concat(#{provinceNameLike,jdbcType=VARCHAR},'%')

        #{item,jdbcType=VARCHAR}

    AND city_id = #{cityId,jdbcType=INTEGER}

        #{item,jdbcType=INTEGER}

    AND city_name = #{cityName,jdbcType=VARCHAR}
    AND city_name LIKE concat(#{cityNameLike,jdbcType=VARCHAR},'%')

        #{item,jdbcType=VARCHAR}

    AND area_id = #{areaId,jdbcType=INTEGER}

        #{item,jdbcType=INTEGER}

    AND area_name = #{areaName,jdbcType=VARCHAR}
    AND area_name LIKE concat(#{areaNameLike,jdbcType=VARCHAR},'%')

        #{item,jdbcType=VARCHAR}

    AND detailed_address = #{detailedAddress,jdbcType=VARCHAR}
    AND detailed_address LIKE concat(#{detailedAddressLike,jdbcType=VARCHAR},'%')

        #{item,jdbcType=VARCHAR}

    AND is_default = #{isDefault,jdbcType=BOOLEAN}

        #{item,jdbcType=BOOLEAN}

    AND create_time >= #{createTimeFrom,jdbcType=TIMESTAMP}
    AND create_time <= #{createTimeTo,jdbcType=TIMESTAMP}
    AND update_time >= #{updateTimeFrom,jdbcType=TIMESTAMP}
    AND update_time <= #{updateTimeTo,jdbcType=TIMESTAMP}
    AND is_deleted = #{isDeleted,jdbcType=BOOLEAN}

        #{item,jdbcType=BOOLEAN}



    order by ${orderBy}
    order by id
